import React, { useState } from "react";

 
export default function SensorImages() {
    const imgUpload = "http://mydrip.ca/wec2022/" + getImgURL(); //data call to get image url

    function getImgURL(){
        var MongoClient = require('mongodb').MongoClient;
        var url = "mongodb://localhost:3000/";

        var imgURL = "";
    
        MongoClient.connect(url, function(err, db) {
        if (err) throw err;
        var dbo = db.db("DripDatabase");
        dbo.collection("historical").findOne({}, function(err, result) {
            if (err) throw err;
            imgURL = result.img;
            db.close();
        });
        return imgURL;
        });
    }

 // This following section will display the form that takes the input from the user.
 // Put HTML here
 return ( 
     <div>
    <p>Sensor Images something</p>
     <img src="http://mydrip.ca/wec2022/uploads/2022.01.09_17:19:13_esp32-cam.jpg"></img>

     <img src={{imgUpload}}></img>

     </div>

 );
}
